using FluentBuilderGenerator.Models;
using FluentBuilderGenerator.Types;

namespace FluentBuilderGenerator.FileGenerators;

// ReSharper disable once InconsistentNaming
internal class IEnumerableBuilderGenerator : IFileGenerator
{
    private const string Name = "FluentBuilder.IEnumerableBuilder.g.cs";

    public FileData GenerateFile()
    {
        return new FileData
        (
            FileDataType.IEnumerableClassBuilder,
            Name,
            @"//------------------------------------------------------------------------------
// <auto-generated>
//     This code was generated by https://github.com/StefH/FluentBuilder.
//
//     Changes to this file may cause incorrect behavior and will be lost if
//     the code is regenerated.
// </auto-generated>
//------------------------------------------------------------------------------

using System;
using System.Collections.Generic;

namespace FluentBuilder
{
    public partial class IEnumerableBuilder<T> : Builder<T[]>
    {
        protected readonly Lazy<List<T>> _list = new(() => new List<T>());

        public virtual IEnumerableBuilder<T> Add(T item) => Add(() => item);
        public virtual IEnumerableBuilder<T> Add(Func<T> func)
        {
            _list.Value.Add(func());

            return this;
        }

        public override T[] Build(bool useObjectInitializer = true)
        {
            if (Object?.IsValueCreated != true)
            {
                Object = new Lazy<T[]>(() =>
                {
                    return _list.Value.ToArray();
                });
            }

            PostBuild(Object.Value);

            return Object.Value;
        }
    }
}"
        );
    }
}